mapper.update:
UPDATE tb_reply
SET
  replyText = #{replyText},
  updateDate = #{updateDate}
WHERE replyNo = #{replyNo}

mapper.select:
SELECT replyNo, articleNo, replyText, replyWriter, regDate, updateDate
FROM tb_reply
WHERE articleNo = #{articleNo}
ORDER BY replyNo
LIMIT #{criteria.pageStart}, #{criteria.perPageNum}

SELECT COUNT(articleNo)
FROM tb_reply
WHERE articleNo = #{articleNo}
